app.get("/", (req, res) => {
  res.send("Node JS api Novelas");
});

app.get("/api/novelas", (req, res) => {
  res.send(novelas);
});

app.post("/api/novelas", (req, res) => {
  const novela = {
    id: novelas.length + 1,
    name: req.body.name,
    description: req.body.description,
    image: req.body.image,
    chapters: {
      1: {
        title: req.body.chapters[1].title,
        contenido: req.body.chapters[1].contenido,
      },
    },
  };
  novelas.push(novela);
  res.send(novela);
});

app.delete("/api/novelas", (req, res) => {
  const novela = novelas.find(c.id == parseInt(req.params.id));
  if (!novelas) return res.status(400).send("Novela no encontrada");

  const index = novelas.indexOf(novela);
  novelas.splice(index, 1);
  res.send(novela);
});

app.post("/api/novelas/:id/capitulos", (req, res) => {
  const novelaId = parseInt(req.params.id);
  const nuevoCapitulo = {
    title: req.body.title,
    contenido: req.body.contenido,
  };
  if (!nuevoCapitulo.title || !nuevoCapitulo.contenido) {
    return res.status(400).json({
      error: "Se requieren título y contenido para el nuevo capítulo",
    });
  }
  const novela = novelas.find((n) => n.id === novelaId);
  if (!novela) {
    return res.status(404).json({ error: "Novela no encontrada" });
  }
  const nuevoNumeroCapitulo = Object.keys(novela.chapters).length + 1;
  novela.chapters[nuevoNumeroCapitulo] = nuevoCapitulo;

  res
    .status(201)
    .json({ message: "Capítulo añadido con éxito", capitulo: nuevoCapitulo });
});

const port = process.env.port || 80;
app.listen(port, () => console.log(`Escuchando en puerto ${port}...`));
